import React, { useState } from 'react';

function Signup() {
  // ... useState 및 핸들러 함수들은 동일 ...

  // 체크 아이콘 컴포넌트
  const CheckIcon = ({ checked }) => (
    <div className={`w-6 h-6 rounded-full flex items-center justify-center border ${
      checked ? 'bg-[#48CBC8] border-[#48CBC8]' : 'bg-white border-gray-300'
    }`}>
      {checked && (
        <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M11.6667 3.5L5.25 9.91667L2.33333 7" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
      )}
    </div>
  );

  return (
    <div className="flex flex-col items-center min-h-screen p-4 bg-white">
      <h1 className="mb-16 text-2xl font-medium">회원가입</h1>

      {/* 입력 폼 부분은 동일 */}
      <div className="w-full max-w-md space-y-4">
        {/* ... 이전 입력 필드들 동일 ... */}

        {/* 약관 동의 섹션 수정 */}
        <div className="mt-6 space-y-4">
          <div className="flex items-center justify-between cursor-pointer" onClick={handleAllCheck}>
            <span className="text-[#48CBC8] font-medium">아래 약관을 모두 동의합니다.</span>
            <CheckIcon checked={agreements.all} />
          </div>

          <div className="flex items-center justify-between cursor-pointer" onClick={() => handleSingleCheck('terms')}>
            <span className="text-gray-500">이용약관 동의 (필수)</span>
            <CheckIcon checked={agreements.terms} />
          </div>

          <div className="flex items-center justify-between cursor-pointer" onClick={() => handleSingleCheck('privacy')}>
            <span className="text-gray-500">개인정보 수집 이용 동의 (필수)</span>
            <CheckIcon checked={agreements.privacy} />
          </div>

          <div className="flex items-center justify-between cursor-pointer" onClick={() => handleSingleCheck('marketing')}>
            <span className="text-gray-500">혜택 | 이벤트 광고 수신 (선택)</span>
            <CheckIcon checked={agreements.marketing} />
          </div>
        </div>

        <button className="w-full p-4 bg-[#B4FFE3] text-[#12918E] rounded-lg text-lg font-medium mt-6">
          가입하기
        </button>
      </div>
    </div>
  );
}

export default Signup;
